import React, {useState, useEffect} from "react";
import ChartComponent, {Bar, Line, Pie} from "react-chartjs-2";
import axios from "axios";

const Validators = () => {
    const [chartData, setChartData] = useState({});
    const [employeeSalary, setEmployeeSalary] = useState([]);
    const [employeeAge, setEmployeeAge] = useState([]);

    const chart = () => {
        let empSal = [];
        let empAge = [];
        let newEmpSal = []
        let newEmpAge = []
        let combinedArray = []
        let newCombinedArray = []

        const query = `
{
  subgraphs(orderDirection: desc
  orderBy: signalledTokens
    first: 10) {
    displayName
    signalledTokens
  }
}
  `;

        axios
            .get("http://139.59.67.100:1317/cosmos/staking/v1beta1/validators")
            .then(res => {
                console.log(res);
                for (const dataObj of res.data.validators) {
                    combinedArray.push(
                        {
                            tokens: (dataObj.tokens),
                            moniker: (dataObj.description.moniker)
                        }
                    )
                    //sorting in descending order
                    combinedArray.sort((a, b) => (a.tokens < b.tokens) ? 1 : -1)
                    newCombinedArray = combinedArray.slice(0, 10)

                    // empSal.push((dataObj.tokens));
                    // newEmpSal = empSal.slice(0,10)
                    // empAge.push((dataObj.description.moniker));
                    // newEmpAge = empAge.slice(0,10)
                }

                for (const dataObj of newCombinedArray) {
                    empSal.push((dataObj.tokens));
                    empAge.push((dataObj.moniker));
                }
                console.log("combined array:", newCombinedArray)
                setChartData({

                    labels: empAge,
                    datasets: [
                        {
                            label: "Amount of uagstake",
                            data: empSal,
                            backgroundColor: ['rgb(255, 99, 132)', 'rgb(0, 255, 0)', 'rgb(204, 153, 255)', 'hsl(210, 100%, 65%)', 'rgb(0, 255, 255)', 'rgb(255, 255, 0)', 'rgb(255, 0, 255)', 'rgb(90, 129, 255)', 'rgb(92, 214, 214)', 'rgb(255, 184, 77)'],

                            borderWidth: 4,
                        }
                    ]
                });
            })
            .catch(err => {
                console.log(err);
            });
        console.log(empSal, empAge);
    };

    useEffect(() => {
        chart();
    }, []);
    return (
        <div className="App">
            <h1>Dankmemes</h1>
            <div>
                <Bar
                    data={chartData}
                    options={{
                        responsive: true,
                        title: {text: "Top 10 Validators by Tokens", display: true},
                        scales: {
                            yAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                }
                            ],
                            xAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        tickLength: 30,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                },

                            ]
                        },
                        layout: {
                            padding: {
                                left: 300,
                                right: 0,
                                top: 0,
                                bottom: 0
                            }
                        }
                    }}
                />
            </div>
        </div>
    );
};

export default Validators;
