import React, {useState, useEffect} from "react";
import ChartComponent, {Bar, Line, Pie, Polar, Radar} from "react-chartjs-2";
import axios from "axios";

const Commission = () => {
    const [chartData, setChartData] = useState({});

    const chart = () => {
        let commission = [];
        let moniker = [];
        let combinedArray = []
        let newCombinedArray = []


         axios
            .get("http://139.59.67.100:1317/cosmos/staking/v1beta1/validators")
            .then(res => {
                for (const dataObj of res.data.validators) {
                    let rewards;
                      axios.get(`http://139.59.67.100:1317/cosmos/distribution/v1beta1/validators/${dataObj.operator_address}/outstanding_rewards`)
                        .then(rewardsRes=> {
                            console.log(rewardsRes)
                            if (rewardsRes.data.rewards.rewards.length > 0) {
                                rewards = rewardsRes.data.rewards.rewards[0].amount
                                console.log("rewards",rewards)
                                console.log("moniker",dataObj.description.moniker)
                                combinedArray.push(
                                    {
                                        commission: (rewards),
                                        moniker: (dataObj.description.moniker)
                                    }
                                )
                            }
                        })
                        .catch(rewardsError => {
                            console.log(rewardsError);
                        })


                    //sorting in ascending order

                    Promise.all(combinedArray).then(() => console.log(combinedArray))
                    combinedArray.sort((a, b) => (a.commission > b.commission) ? 1 : -1)
                    newCombinedArray = combinedArray.slice(0, 10)
                }

                for (const dataObj of newCombinedArray) {
                    commission.push((dataObj.commission));
                    moniker.push((dataObj.moniker));
                }
                console.log("combined array:", newCombinedArray)

            })
            .catch(err => {
                console.log(err);
            });

        setChartData({

            labels: moniker,
            datasets: [
                {
                    label: "Amount of uagstake",
                    data: commission,
                    backgroundColor: ['rgb(255, 99, 132)', 'rgb(0, 255, 0)', 'rgb(204, 153, 255)', 'hsl(210, 100%, 65%)', 'rgb(0, 255, 255)', 'rgb(255, 255, 0)', 'rgb(255, 0, 255)', 'rgb(90, 129, 255)', 'rgb(92, 214, 214)', 'rgb(255, 184, 77)'],

                    borderWidth: 4,
                }
            ]
        });
        console.log(commission, moniker);
    };



    useEffect(() => {
        chart();
    }, []);
    return (
        <div className="App">
            <div>
                <Bar
                    data={chartData}
                    options={{
                        responsive: true,
                        title: {text: "Top 10 Validators by Least Commission", fontSize: 25, display: true},
                        scales: {
                            yAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                }
                            ],
                            xAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        tickLength: 30,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                },

                            ]
                        },
                        layout: {
                            padding: {
                                left: 300,
                                right: 0,
                                top: 0,
                                bottom: 0
                            }
                        }
                    }}
                />
            </div>
        </div>
    );
};

export default Commission;
