import React, {useState, useEffect} from "react";
import ChartComponent, {Bar, Line} from "react-chartjs-2";
import axios from "axios";

const Team = () => {
    const [chartData, setChartData] = useState({});
    const [employeeSalary, setEmployeeSalary] = useState([]);
    const [employeeAge, setEmployeeAge] = useState([]);

    const chart = () => {
        let empSal = [];
        let empAge = [];

        const query = `
{
  delegators(orderDirection: desc
  orderBy: totalStakedTokens
    first: 10) {
    id
    totalStakedTokens
  }
}
  `;


        axios
            .post("https://api.thegraph.com/subgraphs/name/graphprotocol/graph-network-mainnet", {query})
            .then(res => {
                console.log(res);
                for (const dataObj of res.data.data.delegators) {
                    empSal.push(((dataObj.totalStakedTokens)) / Math.pow(10, 18).toFixed(2));
                    empAge.push((dataObj.id));
                }
                setChartData({

                    labels: empAge,
                    datasets: [
                        {
                            label: "Indexing Rewards",
                            data: empSal,
                            backgroundColor: ['rgb(255, 99, 132)', 'rgb(0, 255, 0)', 'rgb(204, 153, 255)', 'hsl(210, 100%, 65%)', 'rgb(0, 255, 255)', 'rgb(255, 255, 0)', 'rgb(255, 0, 255)', 'rgb(90, 129, 255)', 'rgb(92, 214, 214)', 'rgb(255, 184, 77)'],

                            borderWidth: 4,
                        }
                    ]
                });
            })
            .catch(err => {
                console.log(err);
            });
        console.log(empSal, empAge);
    };

    useEffect(() => {
        chart();
    }, []);
    return (
        <div className="App">
            {/*<h1>         Delegator</h1>*/}
            <div>
                <Bar
                    data={chartData}
                    options={{
                        responsive: true,
                        title: {text: "Top 10 Indexers by Indexing Rewards", display: true},
                        scales: {
                            yAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                }
                            ],
                            xAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        tickLength: 30,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                }
                            ]

                        },
                        layout: {
                            padding: {
                                left: 300,
                                right: 0,
                                top: 0,
                                bottom: 0
                            }
                        }
                    }}
                />
            </div>
        </div>
    );
};

export default Team;
