import React, {useState, useEffect} from "react";
import ChartComponent, {Bar, Line, Pie, Polar, Radar} from "react-chartjs-2";
import axios from "axios";

const Commission = () => {
    const [chartData, setChartData] = useState({});

    const chart = () => {
        let commission = [];
        let moniker = [];
        let combinedArray = []
        let newCombinedArray = []
        let axiosRequest = []

        axios
            .get("http://139.59.67.100:1317/cosmos/staking/v1beta1/validators")
            .then(async res => {
                for (const dataObj of res.data.validators) {
                    // const price = await (getPriceInUsd());

                    axiosRequest.push(`http://139.59.67.100:1317/cosmos/distribution/v1beta1/validators/${dataObj.operator_address}/outstanding_rewards`)


                    // await axios.get(`http://139.59.67.100:1317/cosmos/distribution/v1beta1/validators/${dataObj.operator_address}/outstanding_rewards`)
                    //     .then(rewardsRes => {
                    //         console.log(rewardsRes)
                    //         if (rewardsRes.data.rewards.rewards.length > 0) {
                    //             // rewards = rewardsRes.data.rewards.rewards[0].amount
                    //             console.log("rewards", rewardsRes.data.rewards.rewards[0].amount)
                    //             console.log("moniker", dataObj.description.moniker)
                    //             combinedArray.push(
                    //                 {
                    //                     commission: (rewardsRes.data.rewards.rewards[0].amount),
                    //                     moniker: (dataObj.description.moniker)
                    //                 }
                    //             )
                    //
                    //         }
                    //
                    //         }
                    //     )
                    //     .catch(rewardsError => {
                    //         console.log(rewardsError);
                    //     })


                }


                await axios.all(axiosRequest.map(l => axios.get(l))).then(
                    axios.spread((...responses) => {
                            for (const dataObj of responses) {
                                let res = dataObj.config.url;
                                res = res.match(/agoricvaloper.*o/);
                                res = res[0];
                                res = res.substring(0, res.length - 2)
                                console.log("result", res)
                                if (dataObj.data.rewards.rewards.length > 0) {
                                    console.log("rewards", dataObj.data.rewards.rewards[0].amount)
                                    console.log("moniker", res)
                                    combinedArray.push(
                                        {
                                            commission: (dataObj.data.rewards.rewards[0].amount),
                                            moniker: (res)
                                        }
                                    )
                                }


                            }
                            // const responseOne = responses[0];
                            // const responseTwo = responses[1];
                            // const responesThree = responses[2];

                            // use/access the results
                            // console.log(responseOne, responseTwo, responesThree);
                        }
                    ))
                    .catch(errors => {
                        // react on errors.
                        console.error(errors);
                    });
                //
                // for (const dataObj of newCombinedArray) {
                //     commission.push((dataObj.commission));
                //     moniker.push((dataObj.moniker));
                // }

                //sorting in descending order

                combinedArray.sort((a, b) => (a.commission < b.commission) ? 1 : -1)
                console.log("combined array:", combinedArray)
                newCombinedArray = combinedArray.slice(0, 10);
                newCombinedArray.sort((a, b) => (a.commission < b.commission) ? 1 : -1)

                for (const dataObj of newCombinedArray) {
                    commission.push((dataObj.commission));
                    moniker.push((dataObj.moniker));
                }
                console.log("combined array:", newCombinedArray)
                setChartData({
                    labels: moniker,
                    datasets: [
                        {
                            label: "Amount of uagstake",
                            data: commission,
                            backgroundColor: ['rgb(255, 99, 132)', 'rgb(0, 255, 0)', 'rgb(204, 153, 255)', 'hsl(210, 100%, 65%)', 'rgb(0, 255, 255)', 'rgb(255, 255, 0)', 'rgb(255, 0, 255)', 'rgb(90, 129, 255)', 'rgb(92, 214, 214)', 'rgb(255, 184, 77)'],

                            borderWidth: 4,
                        }
                    ]
                });

            })
            .catch(err => {
                console.log(err);
            });


        console.log(commission, moniker);
    };


    useEffect(() => {
        chart();
    }, []);
    return (
        <div className="App">
            <div>
                <Bar
                    data={chartData}
                    options={{
                        responsive: true,
                        title: {text: "Top 10 Validators by Least Commission", fontSize: 25, display: true},
                        scales: {
                            yAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                }
                            ],
                            xAxes: [
                                {
                                    ticks: {
                                        autoSkip: true,
                                        tickLength: 30,
                                        maxTicksLimit: 10,
                                        beginAtZero: true
                                    },
                                    gridLines: {
                                        display: true
                                    }
                                },

                            ]
                        },
                        layout: {
                            padding: {
                                left: 300,
                                right: 0,
                                top: 0,
                                bottom: 0
                            }
                        }
                    }}
                />
            </div>
        </div>
    );
};

export default Commission;
